import React, { useState } from "react";
import {
  signInWithPopup,
  signInWithEmailAndPassword,
  signOut,
} from "firebase/auth";
import { auth, googleProvider, appleProvider } from "./firebase";

function App() {
  const [showLogin, setShowLogin] = useState(false); // State to control popup visibility
  const [email, setEmail] = useState(""); // Email input
  const [password, setPassword] = useState(""); // Password input
  const [user, setUser] = useState(null); // Holds user data

  // Google Login
  const handleGoogleLogin = async () => {
    try {
      const result = await signInWithPopup(auth, googleProvider);
      setUser(result.user);
      alert("Logged in with Google!");
    } catch (error) {
      console.error("Google Login Error:", error.message);
    }
  };

  // Apple Login
  const handleAppleLogin = async () => {
    try {
      const result = await signInWithPopup(auth, appleProvider);
      setUser(result.user);
      alert("Logged in with Apple!");
    } catch (error) {
      console.error("Apple Login Error:", error.message);
    }
  };

  // Email/Password Login
  const handleEmailLogin = async () => {
    try {
      const result = await signInWithEmailAndPassword(auth, email, password);
      setUser(result.user);
      alert("Logged in with Email!");
    } catch (error) {
      console.error("Email Login Error:", error.message);
      alert("Invalid credentials. Please try again.");
    }
  };

  // Logout
  const handleLogout = async () => {
    try {
      await signOut(auth);
      setUser(null); // Clear user data
      alert("Logged out successfully!");
    } catch (error) {
      console.error("Logout Error:", error.message);
    }
  };

  return (
    <div style={styles.container}>
      {/* Header Section */}
      <header style={styles.header}>
        <div style={styles.logo}>EventZen</div>
        <div style={styles.searchBox}>
          <input
            type="text"
            placeholder="Search..."
            style={styles.searchInput}
          />
        </div>
        <div style={styles.authSection}>
          {!user ? (
            <button style={styles.button} onClick={() => setShowLogin(true)}>
              Login
            </button>
          ) : (
            <>
              <span style={styles.welcomeText}>
                Welcome, <strong>{user.displayName || user.email}</strong>
              </span>
              <button style={styles.logoutButton} onClick={handleLogout}>
                Logout
              </button>
            </>
          )}
        </div>
      </header>

      {/* Login Popup */}
      {showLogin && !user && (
        <div style={styles.popup}>
          <h2>Login</h2>
          <button style={styles.loginButton} onClick={handleGoogleLogin}>
            Login with Google
          </button>
          <button style={styles.loginButton} onClick={handleAppleLogin}>
            Login with Apple
          </button>
          <div>
            <h3>Login with Email</h3>
            <input
              style={styles.input}
              type="email"
              placeholder="Email"
              onChange={(e) => setEmail(e.target.value)}
            />
            <input
              style={styles.input}
              type="password"
              placeholder="Password"
              onChange={(e) => setPassword(e.target.value)}
            />
            <button style={styles.loginButton} onClick={handleEmailLogin}>
              Login with Email
            </button>
          </div>
          <button style={styles.closeButton} onClick={() => setShowLogin(false)}>
            Close
          </button>
        </div>
      )}
    </div>
  );
}

// Inline CSS styles
const styles = {
  container: {
    fontFamily: "Arial, sans-serif",
    margin: 0,
    padding: 0,
    color: "#333",
  },
  header: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    backgroundColor: "#282c34",
    color: "white",
    padding: "10px 20px",
  },
  logo: {
    fontSize: "1.5rem",
    fontWeight: "bold",
    flex: "1",
    textAlign: "left",
  },
  searchBox: {
    flex: "2",
    textAlign: "center",
  },
  searchInput: {
    width: "60%",
    padding: "8px",
    borderRadius: "5px",
    border: "1px solid #ccc",
    fontSize: "1rem",
  },
  authSection: {
    flex: "1",
    textAlign: "right",
  },
  welcomeText: {
    marginRight: "10px",
  },
  button: {
    backgroundColor: "#61dafb",
    color: "white",
    border: "none",
    padding: "8px 16px",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "16px",
  },
  logoutButton: {
    backgroundColor: "#f44336",
    color: "white",
    border: "none",
    padding: "8px 16px",
    borderRadius: "5px",
    cursor: "pointer",
    marginLeft: "10px",
    fontSize: "16px",
  },
  popup: {
    backgroundColor: "white",
    border: "1px solid #ccc",
    borderRadius: "10px",
    padding: "20px",
    width: "300px",
    margin: "20px auto",
    boxShadow: "0 0 10px rgba(0, 0, 0, 0.1)",
  },
  loginButton: {
    backgroundColor: "#4285F4",
    color: "white",
    border: "none",
    padding: "10px",
    margin: "5px 0",
    borderRadius: "5px",
    width: "100%",
    cursor: "pointer",
  },
  closeButton: {
    backgroundColor: "#f44336",
    color: "white",
    border: "none",
    padding: "10px",
    borderRadius: "5px",
    cursor: "pointer",
    marginTop: "10px",
  },
  input: {
    width: "100%",
    padding: "10px",
    margin: "5px 0",
    border: "1px solid #ccc",
    borderRadius: "5px",
  },
};

export default App;
