import React, { useState } from "react";
import {
  signInWithPopup,
  signInWithEmailAndPassword,
} from "firebase/auth";
import { auth, googleProvider, appleProvider } from "./firebase";

function App() {
  const [showLogin, setShowLogin] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  // Google Login
  const handleGoogleLogin = async () => {
    try {
      const result = await signInWithPopup(auth, googleProvider);
      console.log("User:", result.user);
      alert("Logged in successfully with Google!");
    } catch (error) {
      console.error("Google Login Error:", error.message);
      alert("Failed to login with Google.");
    }
  };

  // Apple Login
  const handleAppleLogin = async () => {
    try {
      const result = await signInWithPopup(auth, appleProvider);
      console.log("User:", result.user);
      alert("Logged in successfully with Apple!");
    } catch (error) {
      console.error("Apple Login Error:", error.message);
      alert("Failed to login with Apple.");
    }
  };

  // Email/Password Login
  const handleEmailLogin = async () => {
    try {
      const result = await signInWithEmailAndPassword(auth, email, password);
      console.log("User:", result.user);
      alert("Logged in successfully with Email!");
    } catch (error) {
      console.error("Email Login Error:", error.message);
      alert("Failed to login with Email.");
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h1>Welcome to the Login Page</h1>
      <button onClick={() => setShowLogin(true)}>Open Login</button>

      {showLogin && (
        <div style={popupStyles}>
          <h2>Login Options</h2>
          <button onClick={handleGoogleLogin}>Login with Google</button>
          <button onClick={handleAppleLogin}>Login with Apple</button>
          <div>
            <h3>Login with Email</h3>
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button onClick={handleEmailLogin}>Login with Email</button>
          </div>
          <button onClick={() => setShowLogin(false)}>Close</button>
        </div>
      )}
    </div>
  );
}

const popupStyles = {
  border: "1px solid #ccc",
  padding: "20px",
  borderRadius: "10px",
  width: "300px",
  margin: "20px auto",
  background: "#f9f9f9",
};

export default App;
