import React, { useState } from "react";
import {
  signInWithPopup,
  signInWithEmailAndPassword,
  signOut,
} from "firebase/auth";
import { auth, googleProvider, appleProvider } from "./firebase";

function App() {
  const [showLogin, setShowLogin] = useState(false); // State to control popup visibility
  const [email, setEmail] = useState(""); // Email input
  const [password, setPassword] = useState(""); // Password input
  const [user, setUser] = useState(null); // Holds user data
  const [searchQuery, setSearchQuery] = useState(""); // Search query state
  const [location, setLocation] = useState(null); // State to hold user's location
  const [error, setError] = useState(""); // State to hold location errors

  // Google Login
  const handleGoogleLogin = async () => {
    try {
      const result = await signInWithPopup(auth, googleProvider);
      setUser(result.user);
      alert("Logged in with Google!");
    } catch (error) {
      console.error("Google Login Error:", error.message);
    }
  };

  // Apple Login
  const handleAppleLogin = async () => {
    try {
      const result = await signInWithPopup(auth, appleProvider);
      setUser(result.user);
      alert("Logged in with Apple!");
    } catch (error) {
      console.error("Apple Login Error:", error.message);
    }
  };

  // Email/Password Login
  const handleEmailLogin = async () => {
    try {
      const result = await signInWithEmailAndPassword(auth, email, password);
      setUser(result.user);
      alert("Logged in with Email!");
    } catch (error) {
      console.error("Email Login Error:", error.message);
      alert("Invalid credentials. Please try again.");
    }
  };

  // Logout
  const handleLogout = async () => {
    try {
      await signOut(auth);
      setUser(null);
      alert("Logged out successfully!");
    } catch (error) {
      console.error("Logout Error:", error.message);
    }
  };

  // Location Detection
  const detectLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          setLocation({ latitude, longitude });
          setError(""); // Clear errors
        },
        (err) => {
          console.error("Location Error:", err.message);
          setError("Unable to retrieve your location.");
        }
      );
    } else {
      setError("Geolocation is not supported by your browser.");
    }
  };

  // Search Handler
  const handleSearch = () => {
    alert(`You searched for: ${searchQuery}`);
  };

  return (
    <div style={styles.container}>
      {/* Header Section */}
      <header style={styles.header}>
        <div style={styles.logo}>EventZen</div>
        <div style={styles.searchBox}>
          <input
            type="text"
            placeholder="Search..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            style={styles.searchInput}
          />
          <button style={styles.searchButton} onClick={handleSearch}>
            Search
          </button>
        </div>
        <div style={styles.authSection}>
          {!user ? (
            <button style={styles.button} onClick={() => setShowLogin(true)}>
              Login
            </button>
          ) : (
            <>
              <span style={styles.welcomeText}>
                Welcome, <strong>{user.displayName || user.email}</strong>
              </span>
              <button style={styles.logoutButton} onClick={handleLogout}>
                Logout
              </button>
            </>
          )}
        </div>
      </header>

      {/* Navigation Bar */}
      <nav style={styles.navbar}>
        <div style={styles.navLeft}>
          <button style={styles.navButton}>Business Events</button>
          <button style={styles.navButton}>Social Events</button>
          <button style={styles.navButton}>Community Events</button>
          <button style={styles.navButton}>Virtual Events</button>
        </div>
        <div style={styles.navRight}>
          <button style={styles.smallButton} onClick={detectLocation}>
            Detect Location
          </button>
          <button style={styles.smallButton}>Gift Cards</button>
          <button style={styles.smallButton}>Offers</button>
        </div>
      </nav>

      {/* Location Display */}
      {location && (
        <div style={styles.locationDisplay}>
          <p>
            Your location: Latitude: {location.latitude}, Longitude:{" "}
            {location.longitude}
          </p>
        </div>
      )}
      {error && <p style={styles.errorText}>{error}</p>}

      {/* Login Popup */}
      {showLogin && !user && (
        <div style={styles.popup}>
          <h2>Login</h2>
          <button style={styles.loginButton} onClick={handleGoogleLogin}>
            Login with Google
          </button>
          <button style={styles.loginButton} onClick={handleAppleLogin}>
            Login with Apple
          </button>
          <div>
            <h3>Login with Email</h3>
            <input
              style={styles.input}
              type="email"
              placeholder="Email"
              onChange={(e) => setEmail(e.target.value)}
            />
            <input
              style={styles.input}
              type="password"
              placeholder="Password"
              onChange={(e) => setPassword(e.target.value)}
            />
            <button style={styles.loginButton} onClick={handleEmailLogin}>
              Login with Email
            </button>
          </div>
          <button style={styles.closeButton} onClick={() => setShowLogin(false)}>
            Close
          </button>
        </div>
      )}
    </div>
  );
}

// Inline CSS styles
const styles = {
  container: {
    fontFamily: "Arial, sans-serif",
    margin: 0,
    padding: 0,
    color: "#333",
  },
  header: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    backgroundColor: "#282c34",
    color: "white",
    padding: "10px 20px",
  },
  logo: {
    fontSize: "1.5rem",
    fontWeight: "bold",
    flex: "1",
    textAlign: "left",
  },
  searchBox: {
    flex: "2",
    display: "flex",
    justifyContent: "center",
  },
  searchInput: {
    width: "60%",
    padding: "8px",
    borderRadius: "5px 0 0 5px",
    border: "1px solid #ccc",
    fontSize: "1rem",
  },
  searchButton: {
    padding: "8px 16px",
    backgroundColor: "#61dafb",
    color: "white",
    border: "none",
    borderRadius: "0 5px 5px 0",
    cursor: "pointer",
    fontSize: "1rem",
  },
  authSection: {
    flex: "1",
    textAlign: "right",
  },
  navbar: {
    display: "flex",
    justifyContent: "space-between",
    backgroundColor: "#f0f0f0",
    padding: "10px 20px",
  },
  navLeft: {
    display: "flex",
  },
  navRight: {
    display: "flex",
  },
  navButton: {
    backgroundColor: "#61dafb",
    color: "white",
    border: "none",
    padding: "10px 20px",
    marginRight: "10px",
    borderRadius: "5px",
    cursor: "pointer",
  },
  smallButton: {
    backgroundColor: "#ff9800",
    color: "white",
    border: "none",
    padding: "8px 12px",
    marginLeft: "10px",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "14px",
  },
  locationDisplay: {
    marginTop: "20px",
    textAlign: "center",
    fontSize: "1rem",
    color: "#333",
  },
  errorText: {
    color: "red",
    textAlign: "center",
    marginTop: "10px",
  },
  popup: {
    backgroundColor: "white",
    border: "1px solid #ccc",
    borderRadius: "10px",
    padding: "20px",
    width: "300px",
    margin: "20px auto",
    boxShadow: "0 0 10px rgba(0, 0, 0, 0.1)",
  },
};

export default App;
