import React, { useState } from "react";
import { signInWithPopup, signInWithEmailAndPassword } from "firebase/auth";
import { auth, googleProvider, appleProvider } from "./firebase";

function App() {
  const [showLogin, setShowLogin] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  // Google Login
  const handleGoogleLogin = async () => {
    try {
      await signInWithPopup(auth, googleProvider);
      alert("Logged in with Google!");
    } catch (error) {
      console.error(error.message);
    }
  };

  // Apple Login
  const handleAppleLogin = async () => {
    try {
      await signInWithPopup(auth, appleProvider);
      alert("Logged in with Apple!");
    } catch (error) {
      console.error(error.message);
    }
  };

  // Email/Password Login
  const handleEmailLogin = async () => {
    try {
      await signInWithEmailAndPassword(auth, email, password);
      alert("Logged in with Email!");
    } catch (error) {
      console.error(error.message);
    }
  };

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <h1>Welcome to My React Page</h1>
        <p>Click the button below to log in.</p>
        <button style={styles.button} onClick={() => setShowLogin(true)}>
          Login
        </button>
      </header>

      {showLogin && (
        <div style={styles.popup}>
          <h2>Login Options</h2>
          <button style={styles.loginButton} onClick={handleGoogleLogin}>
            Login with Google
          </button>
          <button style={styles.loginButton} onClick={handleAppleLogin}>
            Login with Apple
          </button>
          <div>
            <h3>Login with Email</h3>
            <input
              style={styles.input}
              type="email"
              placeholder="Email"
              onChange={(e) => setEmail(e.target.value)}
            />
            <input
              style={styles.input}
              type="password"
              placeholder="Password"
              onChange={(e) => setPassword(e.target.value)}
            />
            <button style={styles.loginButton} onClick={handleEmailLogin}>
              Login with Email
            </button>
          </div>
          <button style={styles.closeButton} onClick={() => setShowLogin(false)}>
            Close
          </button>
        </div>
      )}
    </div>
  );
}

// Inline CSS styles
const styles = {
  container: {
    fontFamily: "Arial, sans-serif",
    textAlign: "center",
    margin: 0,
    padding: 0,
    color: "#333",
  },
  header: {
    backgroundColor: "#282c34",
    color: "white",
    padding: "20px",
  },
  button: {
    backgroundColor: "#61dafb",
    color: "white",
    border: "none",
    padding: "10px 20px",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "16px",
  },
  popup: {
    backgroundColor: "white",
    border: "1px solid #ccc",
    borderRadius: "10px",
    padding: "20px",
    width: "300px",
    margin: "20px auto",
    boxShadow: "0 0 10px rgba(0, 0, 0, 0.1)",
  },
  loginButton: {
    backgroundColor: "#4285F4",
    color: "white",
    border: "none",
    padding: "10px",
    margin: "5px 0",
    borderRadius: "5px",
    width: "100%",
    cursor: "pointer",
  },
  closeButton: {
    backgroundColor: "#f44336",
    color: "white",
    border: "none",
    padding: "10px",
    borderRadius: "5px",
    cursor: "pointer",
    marginTop: "10px",
  },
  input: {
    width: "100%",
    padding: "10px",
    margin: "5px 0",
    border: "1px solid #ccc",
    borderRadius: "5px",
  },
};

export default App;
